mapper.select:

  		 select yc.p_id , yc.name ,yc.gmt_create,  yc.praise_count , yc.evaluate_count,yc.detail_count,yc.image_main ,tu.u_id ,tu.nick_name from t_user tu , (
		select  tp.p_id , tp.name ,tp.gmt_create,tp.u_id ,tp.praise_count , tp.evaluate_count,tp.detail_count,tp.image_main from t_user_plan up , t_plan tp where up.u_id = #{userId}
    	and up.p_id = tp.p_id and tp.u_id <> #{userId}) yc where tu.u_id = yc.u_id

select  tp.p_id , tp.u_id, tp.name ,tp.gmt_create,tp.descr,image_main,image_main
from  t_plan tp 
where tp.p_id  = #{planId}

   select tcs.* ,count(0) as  count from (
		select tu.nick_name ,t.u_id , tp.p_id , tp.name,tp.descr  from t_user tu ,t_plan tp 
	    where tu.u_id = tp.u_id and tp.p_id = #{planId}  
  	) tcs , t_plan_detail tpd where tcs.p_id = tpd.p_id
